<?php/** * Manage Teatralmente Gioia's public app */namespace backend\controllers;use Yii;use yii\web\Controller;use yii\filters\VerbFilter;use yii\web\Response;use yii\web\JsonResponseFormatter;use yii\web\NotFoundHttpException;use backend\models\Attivita;use backend\models\Prenotazioni;/** * AllegatiController implements the CRUD actions for Allegati model. */class AppPublicController extends Controller{    /**     * @inheritDoc     */    public function behaviors()    {        return array_merge(            parent::behaviors(),            [                'verbs' => [                    'class' => VerbFilter::className(),                    'actions' => [                        'delete' => ['POST'],                    ],                ],            ]        );    }        /**     * Retrivies ALL activities     *      * @return array     */    public function actionAttivita(){        Yii::$app->response->format = Response::FORMAT_JSON;        Yii::$app->response->formatters = [            Response::FORMAT_JSON => [                'class' => JsonResponseFormatter::class,                'prettyPrint' => true            ]        ];                $next = $this->findNext();        $old = $this->findOld();        return [            [                'next' => $next,                'old'  => $old,            ]        ];    }        /**     * Get event info     *      * @param type $id     * @return mixed     */    public function actionAttivitaInfo($id){        Yii::$app->response->format = Response::FORMAT_JSON;        Yii::$app->response->formatters = [            Response::FORMAT_JSON => [                'class' => JsonResponseFormatter::class,                'prettyPrint' => true            ]        ];                $model = $this->findModel($id);        $model->data_attivita = date("d-m-Y h:i");                return $model;    }        /**     * Book an activity for a client.     * An activity is booked on with email (unique value) and a number of place.     * If an error occourred return false, true otherwise.     *      * @param type $id Activity ID     * @param type $email Client Email     * @param type $posti Number of place     * @return bool     */    public function actionAttivitaBook($id, $email, $posti){        Yii::$app->response->format = Response::FORMAT_JSON;        Yii::$app->response->formatters = [            Response::FORMAT_JSON => [                'class' => JsonResponseFormatter::class,                'prettyPrint' => true            ]        ];                $model = $this->findModel($id);        $prenotazioni = new Prenotazioni();                $prenotazioni->attivita_id  = $model->id;        $prenotazioni->email        = $email;        $prenotazioni->prenotazioni = $posti;        if(!$prenotazioni->save()){            return false;        }                return true;    }        /**     * Retrieve a reservation for an user by email and ID.     *      * @param type $id     * @param type $email     * @return type     */    public function actionAttivitaMyBook($id, $email){        Yii::$app->response->format = Response::FORMAT_JSON;        Yii::$app->response->formatters = [            Response::FORMAT_JSON => [                'class' => JsonResponseFormatter::class,                'prettyPrint' => true            ]        ];                $model = $this->findModelByIdAndEmail($id, $email);                        return $model;            }        /**     * Finds the Attivita model (Next activities).     * If the model is not found, a 404 HTTP exception will be thrown.     * @return Attivita the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findNext()    {        if (($model = Attivita::find()->where([            '>=',            'data_attivita',            date('Y-m-d H:i')        ])->orderBy(['data_attivita' => SORT_DESC])->all()) !== null) {            return $model;        }                throw new NotFoundHttpException(Yii::t('app', 'The requested page does not exist.'));    }        /**     * Finds the Attivita model (Old activities).     * If the model is not found, a 404 HTTP exception will be thrown.     * @return Attivita the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findOld()    {        if (($model = Attivita::find()->where([            '<=',            'data_attivita',            date('Y-m-d H:i')        ])->orderBy(['data_attivita' => SORT_DESC])->all()) !== null) {            return $model;        }                throw new NotFoundHttpException(Yii::t('app', 'The requested page does not exist.'));    }        /**     * Finds the Attivita model based on its primary key value.     * If the model is not found, a 404 HTTP exception will be thrown.     * @param string $id ID     * @return Attivita the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findModel($id)    {        if (($model = Attivita::findOne($id)) !== null) {            return $model;        }                throw new NotFoundHttpException(Yii::t('app', 'The requested page does not exist.'));    }        /**     * Finds the Attivita model based on its primary key value and email.     * If the model is not found, a 404 HTTP exception will be thrown.     * @param string $id ID     * @param string $email     * @return Attivita the loaded model     * @throws NotFoundHttpException if the model cannot be found     */    protected function findModelByIdAndEmail($id, $email)    {        if (($prenotazioni = Prenotazioni::find()                                            ->where(['attivita_id' => $id, 'email' => $email])                                            ->one()            ) !== null &&            ($attivita = $this->findModel($id))        ) {            return [                'prenotazioni' => $prenotazioni,                'attivita'     => $attivita,            ];        }                throw new NotFoundHttpException(Yii::t('app', 'The requested page does not exist.'));    }}