ocial = [];            $icona  = [];            $link   = [];                        for($pos = 0; $pos<$size; $pos++){                if(empty($post['SnlSocial']['social'][$pos])){                    continue;                }                                $social_arr[$pos] = new SnlSocial();                if( $post['SnlSocial']['id'][$pos] <> 0){//for update                    $social_arr[$pos] = SnlSocial::findOne($post['SnlSocial']['id'][$pos]);                }                                    $social_arr[$pos]->social   = $post['SnlSocial']['social'][$pos];                $social_arr[$pos]->icona    = $post['SnlSocial']['icona'][$pos];                $social_arr[$pos]->link     = $post['SnlSocial']['link'][$pos];            }                        $save = false;                        foreach ($social_arr as $s){                if($s->save()){                    $save = true;                }else{                    $save = false;                }            }                        if($save){                Yii::$app->session->setFlash('success', Yii::t('app', 'Impostazioni salvate!'));                        return $this->redirect(['/sanlorenzo/settings']);            }        }                return $this->render('settings/index', [            'social'        => $social,            'new_social'    => $new_social,        ]);    }    /********************** END SETTINGS ********************/        /**     * Lists all Partner models.     *     * @return string     */    public function actionPartnerIndex()    {        $searchModel = new \backend\models\SnlPartnerSearch();        $dataProvider = $searchModel->search($this->request->queryParams);        return $this->render('partner/all', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    /**     * Displays a single Partner model.     * @param int $id ID     * @return string     * @throws 