nction actionPartnerUpdate($id)    {        $model = $this->findModelPartner($id);        $old_model = $this->findModelPartner($id);                        if ($this->request->isPost && $model->load($this->request->post())) {            if(($upload = \yii\web\UploadedFile::getInstance($model, 'logo')) ){                $file = 'sanlorenzo'.DIRECTORY_SEPARATOR.'media_uploads'.DIRECTORY_SEPARATOR.md5($upload->baseName.date('d-m-YH:i:s')).".".$upload->extension;                $upload->saveAs($file);                $file = \Yii::$app->params['site_protocol'].\Yii::$app->params['backendWeb'].$file;                $model->logo = $file;            }else{                $model->logo = $old_model->logo;            }                        if($model->save()){                Yii::$app->session->setFlash('success', Yii::t('app', 'Partner aggiornato con successo'));                return $this->redirect(['partner-index']);            }        }        return $this->render('partner/update', [            'model' => $model,            'max'       => $model->ordinamento,        ]);    }    /**     * Deletes an existing Partner model.     * If deletion is successful, the browser will be redirected to the 'index' page.     * @param int $id ID     * @return \yii\web\Response     * @throws NotFoundHttpException if the model cannot be found     */    public function actionPartnerDelete($id)    {        $this->findModelPartner($id)->delete();        Yii::$app->session->setFlash('success', Yii::t('app', 'Partner rimosso con successo'));                return $this->redirect(['partner-index']);    }    ////////// END PARTNER //////////            /////////// JUDGE ///////////    public function actionGiudiciIndex(){        $searchModel = new \backend\models\SnlGiudiciSearch();        $dataProvider = $searchModel->search($this->request->queryParams);        return $this->render('giudici/index', [            'searchModel' => $searchModel,