<?php/** * Controller per la gestione degli spettacoli teatrali della compagnia. *  * Questa sezione Ã¨ utile per gestire le prenotazioni con piantina degli  * spettacoli. *  * @author Mattia Leonardo Angelillo * @date 2024-09-14 */namespace backend\controllers;use Yii;use yii\filters\VerbFilter;use yii\filters\AccessControl;use yii\web\Controller;use backend\models\Spettacoli;use backend\models\SpettacoliSearch;/** * Site controller */class SpettacoliController extends Controller{    /**     * {@inheritdoc}     */    public function behaviors()    {        return [            'access' => [                'class' => AccessControl::className(),                'rules' => [                    [                        'actions' => ['login', 'error','login-by-app', 'reset-password', 'nuova-password'],                        'allow' => true,                    ],                    [                        'actions' => ['logout', 'index'],                        'allow' => true,                        'roles' => ['@'],                    ],                    [                        'actions' => [],//All page                        'allow' => true,                        'roles' => ['Super User', 'spettacoli'],                    ],                ],            ],            'verbs' => [                'class' => VerbFilter::className(),                'actions' => [                    'logout' => ['post'],                ],            ],        ];    }        /**     * {@inheritdoc}     */    public function actions()    {        return [            'error' => [                'class' => 'yii\web\ErrorAction',            ],        ];    }    /**     * Displays homepage.     *     * @return string     */    public function actionIndex()    {        $searchModel = new SpettacoliSearch();        $dataProvider = $searchModel->search($this->request->queryParams);                return $this->render("index", [            'searchModel'  => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    }